def solver(word): 
    word = [ord(c) - ord('a') + 1 for c in word]
    dp = [] 
    res = 0 
    for n in word: 
        res += 1 
        for i in range(len(dp)): 
            dp[i] ^= n
            if dp[i] == n or dp[i] == 0: 
                res += 1 
        dp.append(n)
    return res 



def solver1(word): 
    res = 0 
    dp = []
    for c in word: 
        res += 1 
        for i in range(len(dp)): 
            if c not in dp[i]: 
                if len(dp[i]) == 0: 
                    res += 1
                dp[i].add(c) 
            else: 
                if len(dp[i]) <= 2: 
                    res += 1
                dp[i].remove(c) 
        dp.append(set(c))
    return res


word = "aabb"

# word = ""
print(len(word))
print(solver(word))